#!/usr/bin/env python3\n\"\"\"ملف setup.py\"\"\"\n\nfrom setuptools import setup, find_packages\n\nwith open(\"README.md\", \"r\", encoding=\"utf-8\") as fh:\n    long_description = fh.read()\n\nwith open(\"requirements.txt\", \"r\", encoding=\"utf-8\") as fh:\n    requirements = [line.strip() for line in fh if line.strip() and not line.startswith(\"#\")]\n\nsetup(\n    name=\"5a-diamond-system-pro\",\n    version=\"1.0.0\",\n    author=\"5A System Pro\",\n    author_email=\"info@5asystempro.com\",\n    description=\"نظام احترافي لتحليل تسديدات البلياردو بنظام الدايمند العشري\",\n    long_description=long_description,\n    long_description_content_type=\"text/markdown\",\n    url=\"https://github.com/5ASp/5A-SysPro\",\n    packages=find_packages(where=\"src\"),\n    package_dir={\"\": \"src\"},\n    classifiers=[\n        \"Development Status :: 4 - Beta\",\n        \"Intended Audience :: Developers\",\n        \"Topic :: Games/Entertainment\",\n        \"License :: OSI Approved :: MIT License\",\n        \"Programming Language :: Python :: 3\",\n        \"Programming Language :: Python :: 3.9\",\n        \"Programming Language :: Python :: 3.10\",\n        \"Programming Language :: Python :: 3.11\",\n        \"Programming Language :: Python :: 3.12\",\n    ],\n    python_requires=\">=3.9\",\n    install_requires=requirements,\n    entry_points={\n        \"console_scripts\": [\n            \"billiards-api=api.main:app\",\n        ],\n    },\n)\n"